hudson.tasks.Shell.command:
#!/bin/bash

VERSION="1.0.`date +%s`";

PAYLOAD="{ \"Folder\": \"//telerik.com/resources/AppDirector/Drops/KendoUI\", \"Version\": \"$VERSION\", \"Environment\": \"Live\", \"RepositoryName\": \"Local\", \"Workspace\": \"Documentation\", \"ClientId\": \"uri:platform.cp.builds\", \"ClientSecret\": \"3edd5acdb61db37f3ab2bd735b058911\", \"SkipRegisterPackage\": false, \"OverwritePackage\": true, \"SkipDateChange\": true, \"SkipDeployment\": false, \"DeploymentApiUrl\": \"http://deploy.telerik.com/api/deployment/v1\", \"RepositoryUrl\": \"http://deploy.telerik.com/api/repository/v1\", \"TokenEndpoint\": \"https://identity.telerik.com/v2/oauth/telerik/token\", \"PackageNameFormat\": \"package-{0}-{1}.zip\" }";

echo "posting $PAYLOAD";

curl -X POST -d "$PAYLOAD" -H "Accept: application/json" -H "Content-Type: application/json" http://deploy.telerik.com/api/deployment/v1/packages

RESULT=''

while [ "$RESULT" != "success" ]
do
    if [ "$RESULT" = 'fail' ]
    then
        echo 'Deployment failed :(';
        exit 1;
    else
        RESULT=$(curl -s http://deploy.telerik.com/api/deployment/v1/workspaces/KendoUI/stacks/KendoUI.Docs/environments/Live/status)
        echo "Deployment in progress, current status: $RESULT"
        sleep 2
    fi
done

echo 'Deployment passed :)'
